package com.news.news.models;import lombok.Getter;import lombok.Setter;import javax.persistence.Entity;import javax.persistence.Id;import java.time.LocalDate;@Entity@Getter@Setterpublic class Client {    @Id    private long id;    private String name, mobile_number, text;    private LocalDate date;    public Client() {    }    public Client(String name, String mobile_number, String text) {        this.name = name;        this.mobile_number = mobile_number;        this.text = text;    }}